import React, { useState } from 'react';
import './LoginForm.css';

const LoginForm = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.email) {
      newErrors.email = 'Email l√† b·∫Øt bu·ªôc';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email kh√¥ng h·ª£p l·ªá';
    }
    
    if (!formData.password) {
      newErrors.password = 'M·∫≠t kh·∫©u l√† b·∫Øt bu·ªôc';
    } else if (formData.password.length < 6) {
      newErrors.password = 'M·∫≠t kh·∫©u ph·∫£i c√≥ √≠t nh·∫•t 6 k√Ω t·ª±';
    }
    
    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validateForm();
    
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }
    
    setIsLoading(true);
    
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));
      
      // Here you would typically make an API call to your backend
      console.log('Login data:', formData);
      alert('ƒêƒÉng nh·∫≠p th√†nh c√¥ng! Ch√†o m·ª´ng ƒë·∫øn v·ªõi Travel App!');
      
    } catch (error) {
      console.error('Login error:', error);
      alert('ƒêƒÉng nh·∫≠p th·∫•t b·∫°i. Vui l√≤ng th·ª≠ l·∫°i!');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="brand">
          <h2 className="brand-name">ESCE</h2>
          <p className="brand-sub">Du l·ªãch sinh th√°i</p>
        </div>

        <h3 className="title">ƒêƒÉng nh·∫≠p</h3>
        <p className="subtitle">Nh·∫≠p th√¥ng tin t√†i kho·∫£n ƒë·ªÉ ƒëƒÉng nh·∫≠p</p>

        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <div className="input-wrapper">
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="nh·∫≠p email c·ªßa b·∫°n"
                className={errors.email ? 'error' : ''}
              />
            </div>
            {errors.email && <span className="error-message">{errors.email}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="password">M·∫≠t kh·∫©u</label>
            <div className="input-wrapper with-toggle">
              <input
                type="password"
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                placeholder="nh·∫≠p m·∫≠t kh·∫©u"
                className={errors.password ? 'error' : ''}
              />
              <span className="toggle-icon" aria-hidden>üëÅÔ∏è</span>
            </div>
            {errors.password && <span className="error-message">{errors.password}</span>}
          </div>

          <div className="form-options">
            <label className="remember-me">
              <input type="checkbox" />
              <span className="checkmark"></span>
              Ghi nh·ªõ ƒëƒÉng nh·∫≠p
            </label>
            <a href="/forgot-password" className="forgot-password">Qu√™n m·∫≠t kh·∫©u?</a>
          </div>

          <button
            type="submit"
            className={`login-button ${isLoading ? 'loading' : ''}`}
            disabled={isLoading}
          >
            {isLoading ? (
              <>
                <div className="spinner"></div>
                ƒêang ƒëƒÉng nh·∫≠p...
              </>
            ) : (
              'ƒêƒÉng nh·∫≠p'
            )}
          </button>
        </form>

        <div className="divider"><span>HO·∫∂C</span></div>

        <button className="google-button">
          <span className="g-icon">G</span>
          ƒêƒÉng nh·∫≠p b·∫±ng Google
        </button>

        <div className="signup-link">
          <p>Ch∆∞a c√≥ t√†i kho·∫£n? <a href="#">ƒêƒÉng k√Ω ngay</a></p>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
