import React, { useState, useRef, useEffect } from 'react';
import './OTPVerification.css';

const OTPVerification = () => {
  const [otp, setOtp] = useState(['', '', '', '', '', '']);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [resendTimer, setResendTimer] = useState(60);
  const [canResend, setCanResend] = useState(false);
  const inputRefs = useRef([]);

  useEffect(() => {
    const timer = setInterval(() => {
      setResendTimer((prev) => {
        if (prev <= 1) {
          setCanResend(true);
          clearInterval(timer);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  const handleInputChange = (index, value) => {
    if (value.length > 1) return; // Ch·ªâ cho ph√©p 1 k√Ω t·ª±
    
    const newOtp = [...otp];
    newOtp[index] = value;
    setOtp(newOtp);
    setError('');

    // T·ª± ƒë·ªông chuy·ªÉn sang √¥ ti·∫øp theo
    if (value && index < 5) {
      inputRefs.current[index + 1]?.focus();
    }
  };

  const handleKeyDown = (index, e) => {
    if (e.key === 'Backspace' && !otp[index] && index > 0) {
      inputRefs.current[index - 1]?.focus();
    }
  };

  const handlePaste = (e) => {
    e.preventDefault();
    const pastedData = e.clipboardData.getData('text').replace(/\D/g, '').slice(0, 6);
    const newOtp = [...otp];
    
    for (let i = 0; i < pastedData.length && i < 6; i++) {
      newOtp[i] = pastedData[i];
    }
    
    setOtp(newOtp);
    setError('');
    
    // Focus v√†o √¥ cu·ªëi c√πng ƒë∆∞·ª£c ƒëi·ªÅn
    const lastFilledIndex = Math.min(pastedData.length - 1, 5);
    inputRefs.current[lastFilledIndex]?.focus();
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const otpString = otp.join('');
    
    if (otpString.length !== 6) {
      setError('Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß 6 s·ªë OTP');
      return;
    }

    setLoading(true);
    setError('');

    // Gi·∫£ l·∫≠p g·ª≠i OTP
    await new Promise(r => setTimeout(r, 1500));

    // Gi·∫£ l·∫≠p ki·ªÉm tra OTP (trong th·ª±c t·∫ø s·∫Ω g·ªçi API)
    if (otpString === '123456') {
      // Chuy·ªÉn ƒë·∫øn trang ƒë·ªïi m·∫≠t kh·∫©u
      window.location.href = '/reset-password';
    } else {
      setError('M√£ OTP kh√¥ng ƒë√∫ng. Vui l√≤ng th·ª≠ l·∫°i.');
    }

    setLoading(false);
  };

  const handleResend = async () => {
    setCanResend(false);
    setResendTimer(60);
    setError('');
    
    // Gi·∫£ l·∫≠p g·ª≠i l·∫°i OTP
    await new Promise(r => setTimeout(r, 1000));
    
    const timer = setInterval(() => {
      setResendTimer((prev) => {
        if (prev <= 1) {
          setCanResend(true);
          clearInterval(timer);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="brand">
          <h2 className="brand-name">ESCE</h2>
          <p className="brand-sub">Du l·ªãch sinh th√°i</p>
        </div>

        <div className="otp-icon">üîê</div>
        <h3 className="title">X√°c th·ª±c OTP</h3>
        <p className="subtitle">Nh·∫≠p m√£ OTP 6 s·ªë ƒë√£ ƒë∆∞·ª£c g·ª≠i ƒë·∫øn email c·ªßa b·∫°n</p>

        <form onSubmit={handleSubmit} className="otp-form">
          <div className="otp-inputs">
            {otp.map((digit, index) => (
              <input
                key={index}
                ref={(el) => (inputRefs.current[index] = el)}
                type="text"
                inputMode="numeric"
                pattern="[0-9]*"
                maxLength="1"
                value={digit}
                onChange={(e) => handleInputChange(index, e.target.value)}
                onKeyDown={(e) => handleKeyDown(index, e)}
                onPaste={handlePaste}
                className={`otp-input ${error ? 'error' : ''}`}
                autoComplete="off"
              />
            ))}
          </div>
          
          {error && <span className="error-message">{error}</span>}

          <button 
            type="submit" 
            className={`login-button ${loading ? 'loading' : ''}`} 
            disabled={loading || otp.join('').length !== 6}
          >
            {loading ? <><div className="spinner"></div>ƒêang x√°c th·ª±c...</> : 'X√°c th·ª±c OTP'}
          </button>
        </form>

        <div className="resend-section">
          {canResend ? (
            <button onClick={handleResend} className="resend-button">
              G·ª≠i l·∫°i m√£ OTP
            </button>
          ) : (
            <p className="resend-timer">
              G·ª≠i l·∫°i m√£ sau {resendTimer}s
            </p>
          )}
        </div>

        <a href="/forgot-password" className="fp-back">‚Üê Quay l·∫°i</a>
      </div>
    </div>
  );
};

export default OTPVerification;
