import React, { useState } from 'react';
import './ResetPassword.css';

const ResetPassword = () => {
  const [formData, setFormData] = useState({
    newPassword: '',
    confirmPassword: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [showPassword, setShowPassword] = useState({
    newPassword: false,
    confirmPassword: false
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    setError('');
  };

  const togglePasswordVisibility = (field) => {
    setShowPassword(prev => ({
      ...prev,
      [field]: !prev[field]
    }));
  };

  const validatePassword = (password) => {
    const minLength = password.length >= 8;
    const hasUpperCase = /[A-Z]/.test(password);
    const hasLowerCase = /[a-z]/.test(password);
    const hasNumbers = /\d/.test(password);
    const hasSpecialChar = /[!@#$%^&*(),.?":{}|<>]/.test(password);

    return {
      isValid: minLength && hasUpperCase && hasLowerCase && hasNumbers && hasSpecialChar,
      errors: {
        minLength: !minLength,
        hasUpperCase: !hasUpperCase,
        hasLowerCase: !hasLowerCase,
        hasNumbers: !hasNumbers,
        hasSpecialChar: !hasSpecialChar
      }
    };
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Ki·ªÉm tra m·∫≠t kh·∫©u tr·ªëng
    if (!formData.newPassword || !formData.confirmPassword) {
      setError('Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin');
      return;
    }

    // Ki·ªÉm tra m·∫≠t kh·∫©u kh·ªõp
    if (formData.newPassword !== formData.confirmPassword) {
      setError('M·∫≠t kh·∫©u x√°c nh·∫≠n kh√¥ng kh·ªõp');
      return;
    }

    // Ki·ªÉm tra ƒë·ªô m·∫°nh m·∫≠t kh·∫©u
    const passwordValidation = validatePassword(formData.newPassword);
    if (!passwordValidation.isValid) {
      setError('M·∫≠t kh·∫©u ph·∫£i c√≥ √≠t nh·∫•t 8 k√Ω t·ª±, bao g·ªìm ch·ªØ hoa, ch·ªØ th∆∞·ªùng, s·ªë v√† k√Ω t·ª± ƒë·∫∑c bi·ªát');
      return;
    }

    setLoading(true);

    // Gi·∫£ l·∫≠p g·ª≠i y√™u c·∫ßu ƒë·ªïi m·∫≠t kh·∫©u
    await new Promise(r => setTimeout(r, 2000));

    setLoading(false);
    setSuccess(true);

    // Sau 3 gi√¢y chuy·ªÉn v·ªÅ trang ƒëƒÉng nh·∫≠p
    setTimeout(() => {
      window.location.href = '/login';
    }, 3000);
  };

  if (success) {
    return (
      <div className="login-container">
        <div className="login-card">
          <div className="brand">
            <h2 className="brand-name">ESCE</h2>
            <p className="brand-sub">Du l·ªãch sinh th√°i</p>
          </div>

          <div className="success-icon">‚úÖ</div>
          <h3 className="title">ƒê·ªïi m·∫≠t kh·∫©u th√†nh c√¥ng!</h3>
          <p className="subtitle">M·∫≠t kh·∫©u c·ªßa b·∫°n ƒë√£ ƒë∆∞·ª£c c·∫≠p nh·∫≠t th√†nh c√¥ng</p>
          <p className="redirect-message">ƒêang chuy·ªÉn v·ªÅ trang ƒëƒÉng nh·∫≠p...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="brand">
          <h2 className="brand-name">ESCE</h2>
          <p className="brand-sub">Du l·ªãch sinh th√°i</p>
        </div>

        <div className="reset-icon">üîë</div>
        <h3 className="title">ƒê·∫∑t l·∫°i m·∫≠t kh·∫©u</h3>
        <p className="subtitle">Nh·∫≠p m·∫≠t kh·∫©u m·ªõi c·ªßa b·∫°n</p>

        <form onSubmit={handleSubmit} className="reset-form">
          <div className="input-group">
            <label htmlFor="newPassword">M·∫≠t kh·∫©u m·ªõi</label>
            <div className="password-input-wrapper">
              <input
                id="newPassword"
                name="newPassword"
                type={showPassword.newPassword ? 'text' : 'password'}
                placeholder="Nh·∫≠p m·∫≠t kh·∫©u m·ªõi"
                value={formData.newPassword}
                onChange={handleInputChange}
                className={error ? 'error' : ''}
              />
              <button
                type="button"
                className="password-toggle"
                onClick={() => togglePasswordVisibility('newPassword')}
              >
                {showPassword.newPassword ? 'üôà' : 'üëÅÔ∏è'}
              </button>
            </div>
          </div>

          <div className="input-group">
            <label htmlFor="confirmPassword">X√°c nh·∫≠n m·∫≠t kh·∫©u</label>
            <div className="password-input-wrapper">
              <input
                id="confirmPassword"
                name="confirmPassword"
                type={showPassword.confirmPassword ? 'text' : 'password'}
                placeholder="Nh·∫≠p l·∫°i m·∫≠t kh·∫©u m·ªõi"
                value={formData.confirmPassword}
                onChange={handleInputChange}
                className={error ? 'error' : ''}
              />
              <button
                type="button"
                className="password-toggle"
                onClick={() => togglePasswordVisibility('confirmPassword')}
              >
                {showPassword.confirmPassword ? 'üôà' : 'üëÅÔ∏è'}
              </button>
            </div>
          </div>

          {error && <span className="error-message">{error}</span>}

          <div className="password-requirements">
            <p className="requirements-title">M·∫≠t kh·∫©u ph·∫£i c√≥:</p>
            <ul className="requirements-list">
              <li className={formData.newPassword.length >= 8 ? 'valid' : ''}>
                √çt nh·∫•t 8 k√Ω t·ª±
              </li>
              <li className={/[A-Z]/.test(formData.newPassword) ? 'valid' : ''}>
                M·ªôt ch·ªØ c√°i vi·∫øt hoa
              </li>
              <li className={/[a-z]/.test(formData.newPassword) ? 'valid' : ''}>
                M·ªôt ch·ªØ c√°i vi·∫øt th∆∞·ªùng
              </li>
              <li className={/\d/.test(formData.newPassword) ? 'valid' : ''}>
                M·ªôt s·ªë
              </li>
              <li className={/[!@#$%^&*(),.?":{}|<>]/.test(formData.newPassword) ? 'valid' : ''}>
                M·ªôt k√Ω t·ª± ƒë·∫∑c bi·ªát
              </li>
            </ul>
          </div>

          <button 
            type="submit" 
            className={`login-button ${loading ? 'loading' : ''}`} 
            disabled={loading}
          >
            {loading ? <><div className="spinner"></div>ƒêang c·∫≠p nh·∫≠t...</> : 'ƒê·∫∑t l·∫°i m·∫≠t kh·∫©u'}
          </button>
        </form>

        <a href="/login" className="fp-back">‚Üê Quay l·∫°i ƒëƒÉng nh·∫≠p</a>
      </div>
    </div>
  );
};

export default ResetPassword;
